import React from "react";
import "./App.css";
import { DOMMessage, DOMMessageResponse } from "./types";
import { MemClient } from "@mem-labs/mem-node";
import MDEditor from "@uiw/react-md-editor";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

// "5436519d-2a67-44ac-a1e0-f2952a2c2953"

function App() {
  const [content, setContent] = React.useState<string | undefined>("");
  const [url, setUrl] = React.useState("");
  const [tags, setTags] = React.useState<RegExpMatchArray[]>([]);
  const [apiKey, setApiKey] = React.useState(
    localStorage.getItem("apiKey") || ""
  );
  const [memClient, setMemClient] = React.useState();

  // const memClient = new MemClient({
  //   apiAccessToken: apiKey,
  // });

  const createMem = () => {
    // memClient.createMem({
    //   content: content || "",
    // });
    localStorage.setItem(url, JSON.stringify(content));
  };

  React.useEffect(() => {
    /**
     * We can't use "chrome.runtime.sendMessage" for sending messages from React.
     * For sending messages from React we need to specify which tab to send it to.
     */
    chrome.tabs &&
      chrome.tabs.query(
        {
          active: true,
          currentWindow: true,
        },
        (tabs) => {
          /**
           * Sends a single message to the content script(s) in the specified tab,
           * with an optional callback to run when a response is sent back.
           *
           * The runtime.onMessage event is fired in each content script running
           * in the specified tab for the current extension.
           */
          chrome.tabs.sendMessage(
            tabs[0].id || 0,
            { type: "GET_DOM" } as DOMMessage,
            (response: DOMMessageResponse) => {
              setUrl(response.url);
              setContent(
                localStorage.getItem(response.url)?.slice(1, -1) ||
                  response.content
              );
            }
          );
        }
      );
  }, []);

  const tagRegex = /\B(?<!#)(#[a-zA-Z0-9_]+\b)/g;

  return (
    <div className="App">
      <div>
        <h1>Mem Chrome Extension</h1>
        {apiKey && (
          <Button variant="danger" onClick={createMem}>
            Add to Mem
          </Button>
        )}
      </div>

      {apiKey ? (
        <>
          <div className="container">
            <MDEditor
              value={content}
              onChange={(val) => {
                const match = Array.from(val!.matchAll(tagRegex));
                setContent(val);
                setTags(match);
              }}
              className="markdown"
            />
          </div>

          <div>
            {tags?.map((val) => {
              const hashtag = val[0];
              const tag = val[0].slice(1);
              return (
                <Button
                  size="sm"
                  href={`https://mem.ai/s?filter=%7B%22type%22%3A%22HasTag%22%2C%22tag%22%3A%22${tag.toLowerCase()}%22%2C%22displayName%22%3A%22${tag}%22%7D`}
                  target="_blank"
                  className="tag"
                >
                  {hashtag}
                </Button>
              );
            })}
          </div>
        </>
      ) : (
        <>
          <Form.Label>API Key</Form.Label>
          <Form.Control />
        </>
      )}
    </div>
  );
}

export default App;
