import React from "react";
import "./App.css";
import { DOMMessage, DOMMessageResponse } from "./types";
import { MemClient } from "@mem-labs/mem-node";
import MDEditor from "@uiw/react-md-editor";

const memClient = new MemClient({
  apiAccessToken: "5436519d-2a67-44ac-a1e0-f2952a2c2953",
});

function App() {
  const [content, setContent] = React.useState<string | undefined>("");
  const [url, setUrl] = React.useState("");
  const [tags, setTags] = React.useState<RegExpMatchArray[]>([]);

  const createMem = () => {
    memClient.createMem({
      content: content || "",
    });
    localStorage.setItem(url, JSON.stringify(content));
  };

  React.useEffect(() => {
    /**
     * We can't use "chrome.runtime.sendMessage" for sending messages from React.
     * For sending messages from React we need to specify which tab to send it to.
     */
    chrome.tabs &&
      chrome.tabs.query(
        {
          active: true,
          currentWindow: true,
        },
        (tabs) => {
          /**
           * Sends a single message to the content script(s) in the specified tab,
           * with an optional callback to run when a response is sent back.
           *
           * The runtime.onMessage event is fired in each content script running
           * in the specified tab for the current extension.
           */
          chrome.tabs.sendMessage(
            tabs[0].id || 0,
            { type: "GET_DOM" } as DOMMessage,
            (response: DOMMessageResponse) => {
              setUrl(response.url);
              setContent(
                localStorage.getItem(response.url) || response.content
              );
            }
          );
        }
      );
  }, []);

  const regex = /\B(?<!#)(#[a-zA-Z0-9_]+\b)/g;

  return (
    <div className="App">
      <div>
        <h1>Mem Chrome Extension</h1>
        <button onClick={createMem}>Add to Mem</button>
      </div>

      <div className="container">
        <MDEditor
          value={content}
          onChange={(val) => {
            const match = Array.from(val!.matchAll(regex));
            setContent(val);
            setTags(match);
          }}
        />
      </div>

      <div>
        {tags?.map((tag) => {
          return <h3>{tag[0]}</h3>;
        })}
      </div>
    </div>
  );
}

export default App;
