            const selected = selectAll(selector, tree);
            if (selected && selected.length > 0) {
                visit(tree, selected, (node, index, parent) => {
                    rewrite(node, index, parent);
                });
            }
            return;
        }
        visit(tree, (node, index, parent) => {
            rewrite(node, index, parent);
        });
    };
};
export default remarkRewrite;
