import React from "react";
import "./App.css";
import { DOMMessage, DOMMessageResponse } from "./types";
import { MemClient } from "@mem-labs/mem-node";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";

const memClient = new MemClient({
  apiAccessToken: "5436519d-2a67-44ac-a1e0-f2952a2c2953",
});

function App() {
  const [content, setContent] = React.useState("");

  const createMem = () => {
    memClient.createMem({
      content,
    });
  };

  React.useEffect(() => {
    /**
     * We can't use "chrome.runtime.sendMessage" for sending messages from React.
     * For sending messages from React we need to specify which tab to send it to.
     */
    chrome.tabs &&
      chrome.tabs.query(
        {
          active: true,
          currentWindow: true,
        },
        (tabs) => {
          /**
           * Sends a single message to the content script(s) in the specified tab,
           * with an optional callback to run when a response is sent back.
           *
           * The runtime.onMessage event is fired in each content script running
           * in the specified tab for the current extension.
           */
          chrome.tabs.sendMessage(
            tabs[0].id || 0,
            { type: "GET_DOM" } as DOMMessage,
            (response: DOMMessageResponse) => {
              setContent(response.content);
            }
          );
        }
      );
  }, []);

  const markdown = `Just a link: https://reactjs.com.`;

  return (
    <div className="App">
      <div>
        <h1>Mem Chrome Extension</h1>
        <button onClick={createMem}>Add to Mem</button>
      </div>

      <ul className="SEOForm">{content}</ul>

      <ReactMarkdown># Hello, *world*!</ReactMarkdown>
      <ReactMarkdown children={markdown} remarkPlugins={[remarkGfm]} />
    </div>
  );
}

export default App;
